import React, { useState, useRef, useEffect } from 'react';
import config from '../config/config';

const maxFilesPerDrop = config.maxFilesPerDrop;
const maxFileSizeMB = config.maxFileSizeMB;

function DropZone({ files, onFilesAdded, onFileSelected, onFileDeleted }) {
  const [localFiles, setLocalFiles] = useState(files || []);
  const [currentPage, setCurrentPage] = useState(1);
  const fileInputRef = useRef(null);

  const filesPerPage = 5;
  const totalPages = Math.ceil(localFiles.length / filesPerPage);

  // Sync localFiles si parent change files
  useEffect(() => {
    setLocalFiles(files || []);
  }, [files]);

  const handleFiles = (files) => {
    const fileArray = Array.from(files);

    if (fileArray.length > maxFilesPerDrop) {
      alert(`Vous ne pouvez d√©poser que ${maxFilesPerDrop} fichiers √† la fois.`);
      return; // Ne rien ajouter si trop de fichiers
    }

    // Filtrer fichiers valides (PDF ou images) et par taille
    const validFiles = fileArray.filter((file) => {
      // V√©rifier le type
      const isValidType = file.type === 'application/pdf' || file.type.startsWith('image/');
      
      // V√©rifier la taille (convertir bytes en MB)
      const fileSizeMB = file.size / (1024 * 1024);
      const isValidSize = fileSizeMB <= maxFileSizeMB;
      
      return isValidType && isValidSize;
    });

    // Trouver fichiers invalides par type
    const invalidTypeFiles = fileArray.filter(
      (file) => !(file.type === 'application/pdf' || file.type.startsWith('image/'))
    );

    // Trouver fichiers invalides par taille
    const invalidSizeFiles = fileArray.filter((file) => {
      const isValidType = file.type === 'application/pdf' || file.type.startsWith('image/');
      const fileSizeMB = file.size / (1024 * 1024);
      return isValidType && fileSizeMB > maxFileSizeMB;
    });

    // Afficher les alertes pour fichiers invalides
    if (invalidTypeFiles.length > 0) {
      alert(
        `Ces fichiers ont √©t√© ignor√©s (pas PDF/image) : ${invalidTypeFiles
          .map((f) => f.name)
          .join(', ')}`
      );
    }

    if (invalidSizeFiles.length > 0) {
      alert(
        `Ces fichiers d√©passent la taille maximale de ${maxFileSizeMB}MB : ${invalidSizeFiles
          .map((f) => f.name)
          .join(', ')}`
      );
    }

    if (validFiles.length === 0) {
      return; // Aucun fichier valide
    }

    const updatedList = [...localFiles, ...validFiles];
    setLocalFiles(updatedList);
    onFilesAdded(updatedList);
    setCurrentPage(1);
  };

  const handleDrop = (e) => {
    e.preventDefault();

    if (e.dataTransfer.files.length > maxFilesPerDrop) {
      alert(`Vous ne pouvez d√©poser que ${maxFilesPerDrop} fichiers √† la fois.`);
      return; // Ignore tout le drop
    }

    handleFiles(e.dataTransfer.files);
  };

  const handleClickUpload = () => {
    fileInputRef.current.click();
  };

  const handleDelete = (indexToDelete) => {
    const deletedFile = localFiles[indexToDelete];
    // Demander confirmation avant suppression
    const confirmDelete = window.confirm(`√ätes-vous s√ªr de vouloir supprimer '${deletedFile.name}' ?`);
    if (!confirmDelete) return;

    const updatedFiles = localFiles.filter((_, i) => i !== indexToDelete);
    setLocalFiles(updatedFiles);
    onFilesAdded(updatedFiles);

    if (onFileDeleted) {
      onFileDeleted(deletedFile);
    }

    const newTotalPages = Math.ceil(updatedFiles.length / filesPerPage);
    if (currentPage > newTotalPages) {
      setCurrentPage(newTotalPages || 1);
    }
  };

  const startIndex = (currentPage - 1) * filesPerPage;
  const endIndex = startIndex + filesPerPage;
  const currentFiles = localFiles.slice(startIndex, endIndex);

  return (
    <>
      <div style={{ width: '100%', display: 'flex', alignItems: 'center', justifyContent: 'flex-start', padding: '0px 0 0px 0px', gap: '10px', minHeight: '64px' }}>
        <img 
          src="data:image/webp;base64," 
          alt="OpenBee Logo" 
          style={{ height: '64px', width: '350px' , marginLeft: '-50px' }}
        />
      </div>
      <div />
      <div
        onClick={handleClickUpload}
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        style={{
          height: '150px',
          border: '2px dashed #4a8c5f',
          borderRadius: '10px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          background: '#f9f6f0',
          cursor: 'pointer',
          marginTop: '0px',
          padding: '0'
        }}
      >
        <input
          type="file"
          ref={fileInputRef}
          style={{ display: 'none' }}
          multiple
          accept="application/pdf,image/*"
          onChange={(e) => handleFiles(e.target.files)}
        />
        <span style={{
          fontWeight: 'bold',
          fontSize: '1.15rem',
          background: '#e8f4e8',
          padding: '10px 18px',
          borderRadius: '8px',
          boxShadow: '0 2px 8px rgba(74, 140, 95, 0.15)',
          textAlign: 'center',
          display: 'inline-block',
          color: '#2e6e41',
          border: '1px solid #cce2cc'
        }}>
          üìÇ Cliquer ou glisser-d√©poser des fichiers ici<br />
          (max {maxFilesPerDrop} fichiers, {maxFileSizeMB}MB chacun)
        </span>
      </div>

      <div className="mt-3">
        {currentFiles.map((file, index) => {
          const fileSizeMB = (file.size / (1024 * 1024)).toFixed(2);
          return (
            <div
              key={startIndex + index}
              className="p-2 border rounded mb-2 d-flex justify-content-between align-items-center"
              style={{ 
                background: '#e8f4e8', 
                border: '1px solid #cce2cc !important',
                color: '#2e6e41'
              }}
            >
              <div>
                <span
                  style={{ cursor: 'pointer', fontWeight: 'bold' }}
                  onClick={() => onFileSelected(file)}
                >
                  {file.name}
                </span>
                <div style={{ fontSize: '0.8rem', color: '#4a8c5f' }}>
                  {fileSizeMB} MB
                </div>
              </div>
              <button
                onClick={() => handleDelete(startIndex + index)}
                style={{
                  background: 'none',
                  border: 'none',
                  fontSize: '1.2rem',
                  cursor: 'pointer',
                  color: '#2e6e41'
                }}
                title="Supprimer"
              >
                üóëÔ∏è
              </button>
            </div>
          );
        })}

        {totalPages > 1 && (
          <div className="d-flex justify-content-between align-items-center mt-2">
            <button
              onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))}
              disabled={currentPage === 1}
              className="btn btn-sm"
              style={{
                backgroundColor: '#4a8c5f',
                color: 'white',
                border: 'none'
              }}
            >
              ‚¨ÖÔ∏è Pr√©c√©dent
            </button>
            <span style={{ color: '#2e6e41', fontWeight: 'bold' }}>
              Page {currentPage} sur {totalPages}
            </span>
            <button
              onClick={() => setCurrentPage((p) => Math.min(p + 1, totalPages))}
              disabled={currentPage === totalPages}
              className="btn btn-sm"
              style={{
                backgroundColor: '#4a8c5f',
                color: 'white',
                border: 'none'
              }}
            >
              Suivant ‚û°Ô∏è
            </button>
          </div>
        )}
      </div>
    </>
  );
}

export default DropZone;